import React, {useState} from 'react';
import {Responsive, WidthProvider} from 'react-grid-layout';

const ResponsiveGridLayout = WidthProvider(Responsive);

function App() {

    const [layouts, setLayouts] = useState([
        {i: 'a', x: 0, y: 0, w: 1, h: 2, static: true},
        {i: 'b', x: 1, y: 0, w: 3, h: 2, minW: 2, maxW: 4},
        {i: 'c', x: 4, y: 0, w: 1, h: 2}
    ]);

    const generateLayout = () => {
        layouts.forEach((item) => {
            return item
        })
    }

    const defaultState = {
        layouts: generateLayout(),
        onLayoutChange: () => {
        },
        mounted: false,
        currentBreakpoint: {lg: generateLayout()},
        margin: [10, 10],
        breakpoints: {lg: 1200, md: 996, sm: 768, xs: 480, xxs: 0},
        cols: {lg: 12, md: 10, sm: 6, xs: 4, xxs: 2},
        verticalCompact: false,
        compactType: null,
        autoSize: false
    }


    const [state, setState] = useState(defaultState);

    const onBreakpointChange = (breakpoint) => {
        console.log(breakpoint)
        setState({
            ...state,
            currentBreakpoint: breakpoint
        })
    };

    const onDrop = (elemParams) => {
        console.log(`Element parameters: ${JSON.stringify(elemParams)}`);
    };


    const onLayoutChange = (layout, layouts) => {
        state.onLayoutChange(layout, layouts);
    };

    const generateDOM = () => {
        layouts.map((layout,index) => {
            return (
                <div key={index}
                     className={layout.static ? 'static' : ''}
                >
                    <span className="text">{index}</span>
                </div>
            )
        })
    }

    return (
        <ResponsiveGridLayout className="layout"
                              {...state}
                              onBreakpointChange={onBreakpointChange}
                              onLayoutChange={onLayoutChange}
                              onDrop={onDrop}
        >
            {generateDOM()}
        </ResponsiveGridLayout>
    )
}

export default App;
