import React, {useEffect, useState} from "react"
import {Rnd} from "react-rnd";

const style = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    border: "solid 1px #ddd",
    background: "#f0f0f0",
};

const App = () => {

    const [transformNote, setTransformNote] = useState(false);
    const [text, setText] = useState('');

    const [state, setState] = useState({
        width: 200,
        height: 200,
        x: 10,
        y: 10
    });

    useEffect(() => {
        console.log("change")
        if (state.x !== 0 || state.y !== 0 ){
            setState((previous) => {
                return {...previous, x: 0, y: 0}
            })
        }
    }, [transformNote])

    return (
            <Rnd
                className={`${transformNote ? 'note' : ''}`}
                disableDragging={transformNote}
                style={{...style,transition: transformNote ? "all 1s": ''}}
                size={{width: state.width, height: state.height}}
                position={transformNote ? JSON.parse(localStorage.getItem('position')) || {x: state.x, y: state.y}
                : {x: state.x, y: state.y}
                }
                onDragStop={(e, d) => {
                    setState({x: d.x, y: d.y});
                    if (transformNote) localStorage.setItem('position', JSON.stringify({x: d.x, y: d.y}))
                }}
                onResizeStop={(e, direction, ref, delta, position) => {
                    setState({
                        width: ref.style.width,
                        height: ref.style.height,
                        ...position
                    });
                }}
            >
                {
                    transformNote
                        ?
                        (
                            <textarea
                                className="h-100 w-100"
                                style={{resize: 'none'}}
                                value={text}
                                onChange={(e) => setText(e.target.value)}
                                onDoubleClick={() => setTransformNote(false)}
                            >

                            </textarea>
                        )
                        : (
                            <div className="h-100 w-100"
                                 style={{backgroundColor: 'white'}}
                                 onDoubleClick={() => setTransformNote(true)}
                            >
                                {text}
                            </div>
                        )
                }
            </Rnd>
    )
}

export default App
