import React, {useEffect, useState} from "react"
import {Rnd} from "react-rnd";

const style = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    border: "solid 1px #ddd",
    background: "#f0f0f0",
};

const App = () => {

    const [transformNote, setTransformNote] = useState(false);
    const [text, setText] = useState('');

    const [state, setState] = useState({
        width: 200,
        height: 200,
        x: 10,
        y: 10
    });

    const savePositionWhenTransformation = () => {
        console.log("change", state.x !== 0)
        if (state.x !== 0 || state.y !== 0) {
            localStorage.setItem('position', JSON.stringify({x: state.x, y: state.y}))
            setState(
                {...state, x: 0, y: 0}
            )
        } else if (state.x === 0 && state.y === 0) {
            const stringPosition = localStorage.getItem('position');
            if (stringPosition) {
                const {x, y} = JSON.parse(stringPosition);
                setState({
                    ...state,
                    x, y
                })
            }
        }
    };

    useEffect(savePositionWhenTransformation, [transformNote])


    const onDragStopHandler = (event, data) => {
        setState({x: data.x, y: data.y})
    }

    const onResizeStopHandler = (e, direction, ref, delta, position) => {
        console.log("direction", direction);
        console.log("ref", ref);
        console.log('delta', delta)
        console.log("position", position);
        setState({
            width: ref.style.width,
            height: ref.style.height,
            ...position
        });
    }

    return (
        <Rnd
            className={`${transformNote ? 'note' : ''}`}
            disableDragging={transformNote}
            style={{...style, transition: transformNote ? "all 1s" : ''}}
            size={{width: state.width, height: state.height}}
            position={{x: state.x, y: state.y}}
            onDragStop={onDragStopHandler}
            onResizeStop={onResizeStopHandler}
        >
            {
                transformNote
                    ?
                    (
                        <textarea
                            className="h-100 w-100"
                            style={{resize: 'none'}}
                            value={text}
                            onChange={(e) => setText(e.target.value)}
                            onDoubleClick={() => setTransformNote(false)}
                        >

                            </textarea>
                    )
                    : (
                        <div className="h-100 w-100"
                             style={{backgroundColor: 'white'}}
                             onDoubleClick={() => setTransformNote(true)}
                        >
                            {text}
                        </div>
                    )
            }
        </Rnd>
    )
}

export default App
