import React, {useEffect, useState} from "react";
import GridLayout from 'react-grid-layout';
const App = ({node}) => {
    const layouts = [
        {i: 'a', x: 0, y: 0, w: 1, h: 2, static: true},
        {i: 'b', x: 1, y: 0, w: 3, h: 2, minW: 2, maxW: 4},
        {i: 'c', x: 4, y: 0, w: 1, h: 2}
    ];

    useEffect(()=> {
        console.log(node)
    })

    const renderDOM = () => {
        return layouts.map((layout,index) => {
            console.log("Rerender ?")
            return (
                <div key={layout.i} style={{backgroundColor: 'rgba(0, 50, 50, 0.0)'}}>
                    <div className="card" style={{width: `${89 * layout.w}px`}}>
                        {layout.x}
                    </div>
                </div>
            )
        })
    }

    return (
        <GridLayout className="layout"
                    layout={layouts}
                    cols={12}
                    rowHeight={30}
                    width={1200}
        >
            {renderDOM()}

        </GridLayout>
    )
}

export default App
