import React, {useEffect, useState} from "react"
import Note from "./components/Note";


const App = () => {

    const [state, setState] = useState([
        {
            width: 200,
            height: 200,
            x: 0,
            y: 0
        },
        {
            width: 200,
            height: 200,
            x: 0,
            y: 0
        }
    ]);

    const localKey = (index) => `position-note-${index}`;
    const saveInLocal = (index, note) =>
        localStorage.setItem(localKey(index), JSON.stringify(note))

    const getFromLocal = (index) => localStorage.getItem(localKey(index))

    // const removeInLocal = (index) =>
    //     localStorage.removeItem(localKey(index))


    useEffect(() => {
        setState(state.map((note, index) => {
            const fromLocal = getFromLocal(index);
            if (fromLocal)
                return {...JSON.parse(fromLocal)}
            else
                return note
        }))
    }, [])

    const [socket,setSocket] = useState(null);

    const testHandler = () => {
        const webSocket = new WebSocket('ws://localhost:8080');
        webSocket.onopen = (ev) => {
            //todo : get actual notes position
            console.log('open',ev.data);
        }
        webSocket.onmessage = (ev) => {
            // todo : add handler adding actual position in runtime
            console.log("message",ev.data);
        }
        setSocket(webSocket)
    }

    const send = ( ) => {
        socket.send("Some string")
    }

    return (
        <div style={{backgroundColor: "aqua", width: "100vw", height: '100vh'}}>
            {
                state.map((note, index) => {
                        return (
                            <Note key={index}
                                  index={index}
                                  state={state}
                                  setState={setState}
                                  saveInLocal={saveInLocal}
                                  getFromLocal={getFromLocal}
                            />
                        )
                    }
                )
            }
        </div>
    )
}


export default App
