import React, {useEffect, useRef, useState} from "react"
import {Rnd} from "react-rnd"

const style = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    border: "solid 1px #ddd",
    background: "#f0f0f0",
};

const Note = ({ index, state, setState}) => {


    useEffect(() => {
        console.log('state',index,state[index]);
    }, [state])

    const [transformNote, setTransformNote] = useState(false);
    const [text, setText] = useState('');
    const rnd = useRef();


    const savePositionWhenTransformation = () => {
        if (transformNote && (state[index].x !== 0 || state[index].y !== 0)) {
            localStorage.setItem(`position-note-${index}`, JSON.stringify({x: state[index].x, y: state[index].y}))
            console.log("save position",index)
            let newState = [...state]
            newState[index] = {...state[index], x: 0, y: 0}
            setState(newState)
        } else if (state[index].x === 0 && state[index].y === 0) {
            console.log("get position" ,index)
            const stringPosition = localStorage.getItem(`position-note-${index}`);
            if (stringPosition) {
                const {x, y} = JSON.parse(stringPosition);
                let newState = [...state]
                newState[index] = {...state[index], x, y}
                setState(newState)
            }
        }
    };

    const getPositionIfRefreshPage = () => {
        console.log("refresh")
        if (state.x === 0 && state.y === 0) {
            console.log("get when refresh",index)
            const stringPosition = localStorage.getItem(`position-note-${index}`);
            if (stringPosition) {
                const {x, y} = JSON.parse(stringPosition);
                let newState = [...state]
                newState[index] = {...state[index], x, y}
                setState(newState)
            }
        }
    }

    useEffect(getPositionIfRefreshPage, [])
    useEffect(savePositionWhenTransformation, [transformNote])


    const onDragStopHandler = (event, data) => {
        console.log("on drag stop",index)
        let newState = [...state]
        newState[index] = {...state[index], x: data.x, y: data.y}
        setState(newState)
    }

    const onResizeStopHandler = (e, direction, ref, delta, position) => {
        console.log("resize old",state[index],index)
        let newState = [...state]
        newState[index] = {
            width: ref.style.width,
            height: ref.style.height,
            ...position
        }
        console.log("resize new",newState[index],index)
        setState(newState);
    }

    return (
        <Rnd
            ref={rnd}
            className={`note${transformNote ? '-activate' : ''}`}
            disableDragging={transformNote}
            style={{...style}}
            size={{width: state[index].width, height: state[index].height}}
            minWidth={250}
            minHeight={250}
            position={{x: state[index].x, y: state[index].y}}
            onDragStop={onDragStopHandler}
            onResizeStop={onResizeStopHandler}
            bounds={"window"}
        >
            <div className="note-header">
                <div className="color-buttons">
                    <button className="btn btn-success"/>
                    <button className="btn btn-danger"/>
                    <button className="btn btn-secondary"/>
                </div>
                <div className="cross">
                    &times;
                </div>
            </div>
            {
                transformNote
                    ?
                    (
                        <textarea
                            className="note-text"
                            style={{resize: 'none', backgroundColor: "grey", width: '100%', height: "100%"}}
                            value={text}
                            onChange={(e) => setText(e.target.value)}
                            onDoubleClick={() => setTransformNote(false)}
                        >

                            </textarea>
                    )
                    : (
                        <div className="note-body"
                             style={{backgroundColor: 'grey'}}
                             onDoubleClick={() => setTransformNote(true)}
                        >
                            {text}
                        </div>
                    )
            }
        </Rnd>
    )
}

export default Note
