import React, {useEffect, useRef, useState} from "react"
import {Rnd} from "react-rnd"
import useNote from "../hooks/note.hook";
import useLocal from "../hooks/local.hook";
import {TypeMessage} from '../hooks/TypeMessage'

const style = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    border: "solid 1px #ddd",
    background: "#f0f0f0",
};

const Note = ({height, width, x, y, noteId, color, serverText, notes, setNotes, connection}) => {


    const rnd = useRef();
    const [transformNote, setTransformNote] = useState(false);
    const [text, setText] = useState(serverText);
    const {removeInLocal, saveInLocal, getFromLocal} = useLocal();
    const {updateNote} = useNote();

    const getNote = () => notes.find(note => note.id === noteId)
    const updateStateNotes = updatedNote => setNotes(notes.splice(noteId, updatedNote))
    const sendNewPosition = (note, type) => connection.send(JSON.stringify({...note, type}))

    const savePositionWhenTransformation = () => {
        const note = getNote();
        if (transformNote && (x !== 0 || y !== 0)) {
            const updatedNote = updateNote(note, {x, y});
            saveInLocal(noteId, updatedNote)
            updateStateNotes({...note, x: 0, y: 0});
        } else if (x === 0 && y === 0) {
            const stringPosition = getFromLocal(noteId)
            if (stringPosition) {
                const parse = JSON.parse(stringPosition);
                const updatedNote = updateNote(note, {
                    width: parse.width,
                    height: parse.height,
                    x: parse.x,
                    y: parse.y
                });
                setNotes(updatedNote)
            }
        }
    };

    useEffect(savePositionWhenTransformation, [transformNote])


    const onDragStopHandler = (event, data) => {
        const note = getNote();
        const updatedNote = updateNote(note, {x: data.x, y: data.y});
        saveInLocal(noteId, updatedNote)
        updateStateNotes(updatedNote)
        sendNewPosition(updatedNote,TypeMessage.UPDATE)
    }

    const onResizeStopHandler = (e, direction, ref, delta, position) => {
        const updatedNote = updateNote(getNote(), {
            width: ref.style.width,
            height: ref.style.height,
            x: position.x,
            y: position.y
        });
        updateStateNotes(updatedNote)
        sendNewPosition(updatedNote,TypeMessage.UPDATE)
    }

    const deleteNoteHandler = () => {
        // todo: create delete function on cross icon
        console.log("Nothing to do")
    }

    return (
        <Rnd
            ref={rnd}
            className={`note${transformNote ? '-activate' : ''}`}
            disableDragging={transformNote}
            style={{...style}}
            size={{width, height}}
            minWidth={250}
            minHeight={250}
            position={{x, y}}
            onDragStop={onDragStopHandler}
            onResizeStop={onResizeStopHandler}
            bounds={"window"}
        >
            <div className="note-header">
                <div className="color-buttons">
                    <button className="btn btn-success"/>
                    <button className="btn btn-danger"/>
                    <button className="btn btn-secondary"/>
                </div>
                <div className="cross"
                     onClick={deleteNoteHandler}
                >
                    &times;
                </div>
            </div>
            {
                transformNote
                    ?
                    (
                        <textarea
                            className="note-text"
                            style={{ backgroundColor: color || "grey"}}
                            value={text}
                            onChange={(e) => setText(e.target.value)}
                            onDoubleClick={() => setTransformNote(false)}
                        >

                            </textarea>
                    )
                    : (
                        <div className="note-body"
                             style={{backgroundColor: color || "grey"}}
                             onDoubleClick={() => setTransformNote(true)}
                        >
                            {text}
                        </div>
                    )
            }
        </Rnd>
    )
}

export default Note
