import React, {useEffect, useRef, useState} from "react"
import {Rnd} from "react-rnd"
import update from 'immutability-helper'

const style = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    border: "solid 1px #ddd",
    background: "#f0f0f0",
};

const App = () => {

    const [transformNote, setTransformNote] = useState(false);
    const [text, setText] = useState('');
    const rnd = useRef();

    const [state, setState] = useState({
        width: 200,
        height: 200,
        x: 0,
        y: 0
    });

    const savePositionWhenTransformation = () => {
        console.log("change", state.x !== 0)
        const notNull = state.x !== 0 || state.y !== 0;
        if (notNull) {
            localStorage.setItem('position', JSON.stringify({x: state.x, y: state.y}))
            // rnd.updatePosition({x:0,y:0})
            while (notNull)
            setState( (prevState) =>{
                return {...state, x: state.x--, y: state.y--}
            }

        )
        } else if (state.x === 0 && state.y === 0) {
            const stringPosition = localStorage.getItem('position');
            if (stringPosition) {
                const {x, y} = JSON.parse(stringPosition);
                // rnd.updatePosition({x,y})
                setState({
                    ...state,
                    x, y
                })
            }
        }
    };

    const getPositionIfRefreshPage = () => {
        if (state.x === 0 && state.y === 0) {
            const stringPosition = localStorage.getItem('position');
            if (stringPosition) {
                const {x, y} = JSON.parse(stringPosition);
                // rnd.updatePosition({x,y})
                setState({
                    ...state,
                    x, y
                })
            }
        }
    }

    useEffect(getPositionIfRefreshPage, [])
    useEffect(savePositionWhenTransformation, [transformNote])


    const onDragStopHandler = (event, data) => {
        setState(update(state,
            {$merge: {x: data.x, y: data.y}}
            )
        )
    }

    const onResizeStopHandler = (e, direction, ref, delta, position) => {
        console.log("direction", direction);
        console.log("ref", ref);
        console.log('delta', delta)
        console.log("position", position);
        setState({
            width: ref.style.width,
            height: ref.style.height,
            ...position
        });
    }

    return (
        <Rnd
            className={`${transformNote ? 'note' : ''}`}
            disableDragging={transformNote}
            style={{...style, transition: 'width 1s, height 1s'}}
            size={{width: state.width, height: state.height}}
            minWidth={150}
            minHeight={150}
            position={{x: state.x, y: state.y}}
            onDragStop={onDragStopHandler}
            onResizeStop={onResizeStopHandler}
        >
            <div className="row color-buttons">
                <button className="btn btn-success"/>
                <button className="btn btn-danger"/>
                <button className="btn btn-secondary"/>
            </div>
            <div className="cross">
                &times;
            </div>
            {
                transformNote
                    ?
                    (
                        <textarea
                            className="card h-100 w-100"
                            style={{resize: 'none', backgroundColor: "grey"}}
                            value={text}
                            onChange={(e) => setText(e.target.value)}
                            onDoubleClick={() => setTransformNote(false)}
                        >

                            </textarea>
                    )
                    : (
                        <div className="h-100 w-100"
                             style={{backgroundColor: 'grey'}}
                             onDoubleClick={() => setTransformNote(true)}
                        >
                            {text}
                        </div>
                    )
            }
        </Rnd>
    )
}

export default App
