import {useCallback, useEffect, useState} from "react";
import {TypeMessage} from "./TypeMessage";


const useWS = (notes, setNotes) => {

    const [connection, setConnection] = useState(null);

    const update = useCallback((serverNote) => {
        setNotes((prev) => [...prev.filter(note => note.id !== serverNote.id), serverNote])
    }, [setNotes])

    const reducers = {
        [TypeMessage.CREATE]: update,
        [TypeMessage.UPDATE]: update,
        [TypeMessage.DELETE]: (respNote) => setNotes(notes.filter(note => note.id !== respNote.id)),
    }

    const reducer = (note,action) => {
        switch (action) {
            case TypeMessage.CREATE:
            case TypeMessage.UPDATE:
                return update
        }
    }

    const noteReducer = (respNote, action) => {
        if (Array.isArray(respNote)) {
            setNotes(() => respNote)
        } else {
            reducers[action](respNote)
        }
    }

    const updatePosition = useCallback(({data}) => {
        if (data) {
            const respNote = JSON.parse(data);
            noteReducer(respNote,respNote.type)
        }
    }, [notes, setNotes, update])

    const connected = () => {
        console.log("connected")
    }

    useEffect(() => {
        if (connection === null) {
            const url = window.location.href
                .replace(/^http/, 'ws')
                .replace(/3000\/$/, '5000/');
            console.log(url)
            const webSocket = new WebSocket(url)
            webSocket.onmessage = updatePosition
            webSocket.onopen = connected
            setConnection(webSocket)
        }
    }, [connection, updatePosition])

    return {connection}
}

export default useWS
