import React from 'react';
import {useDrag} from 'react-dnd';
import {ItemTypes} from './types';

const style = {
    position: 'absolute',
    width: "100px",
    height: "50px",
    border: '1px dashed gray',
    backgroundColor: 'white',
    padding: '0.5rem 1rem',
    cursor: 'move',
};
export const Box = ({id, left, top, children,}) => {
    const [{isDragging}, drag] = useDrag({
        item: {id, left, top, type: ItemTypes.NOTE},
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    });
    if (isDragging) {
        return <div ref={drag}/>;
    }
    return (
        <div ref={drag} className="card" style={{...style, left, top}}>
            <div className="card-body">
                <h5 className="card-title">{children}</h5>
                <h6 className="card-subtitle mb-2 text-muted">Подзаголовок карты</h6>
                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the
                    card's content.</p>
                <a href="#" className="card-link">Ссылка карты</a>
                <a href="#" className="card-link">Другая ссылка</a>
            </div>

        </div>
    );
};
