import React, {useCallback, useEffect, useState} from "react"
import Note from "./components/Note";
import useWS from "./hooks/ws.hook";
import useNote from "./hooks/note.hook";
import {TypeMessage} from "./hooks/TypeMessage";
import useLocal from "./hooks/local.hook";


const App = () => {


    const {defaultNote, updateNote} = useNote();
    const [notes, setNotes] = useState([]);
    const {saveInLocal, getFromLocal, removeInLocal} = useLocal();

    useEffect(function watchNotes() {
        console.log("updated on mounted notes App", notes)

        return () => {
            console.log("updated on unmounted notes App", notes)
            setNotes(notes)
        }
    }, [notes])

    function setArrayNotes(serverNote) {
        serverNote.forEach(note => {
            saveInLocal(note.id, JSON.stringify(note))
        })
        setNotes(serverNote)
    }

    useEffect(function watchMounting() {
        console.log('mount')
        return () => {
            console.log("unmount app and update notes")
            setNotes(notes)
        }
    }, [])

    const compare = (localNote, serverNote) => localNote.x === serverNote.x
        && localNote.y === serverNote.y
        && localNote.width === serverNote.width
        && localNote.height === serverNote.height


    const update = useCallback((serverNote, data) => {
        console.log('Update: old notes', notes, "add", serverNote)
        saveInLocal(serverNote.id, data)
        const index = notes.findIndex(note => note.id === serverNote.id);
        const fromLocal = getFromLocal(serverNote.id);
        const parse = JSON.parse(fromLocal);
        // if (!compare(parse, serverNote)) {
        console.log(notes.length, serverNote)
        if (~notes.length) {
            notes[index] = serverNote
            setNotes([...notes])
        } else {
            setNotes([serverNote])
        }
        // }
    }, [notes])

    const updatePosition = ({data}) => {
        if (data) {
            const respNote = JSON.parse(data);
            console.log('update positions: resp', data, 'old notes', notes)
            if (!respNote.type && Array.isArray(respNote)) {
                console.log("initial")
                setArrayNotes(respNote);
                return;
            }
            if (respNote.type === TypeMessage.UPDATE) {
                console.log(TypeMessage.UPDATE)
                update(respNote, data);
            } else if (respNote.type === TypeMessage.CREATE) {
                console.log(TypeMessage.CREATE)
                setNotes([...notes, respNote])
                update(respNote, data);
            } else if (respNote.type === TypeMessage.DELETE) {
                removeInLocal(respNote.id)
                setNotes(notes.filter(note => note.id !== respNote.id))
            } else {
                console.error('Incorrect data message', data)
            }
        }
    }

    const {connection} = useWS(updatePosition);



    // useEffect(() => {
    //     console.log(notes.length);
    // }, [notes])

    const createHandler = (e) => {
        const newNote = updateNote(defaultNote, {x: e.clientX, y: e.clientY, type: TypeMessage.CREATE});
        connection.send(JSON.stringify(newNote))
    }

    return (
        <div style={{backgroundColor: "aqua", width: "100vw", height: '100vh', cursor: 'pointer'}}
             onDoubleClick={createHandler}
        >
            {notes &&
            notes.map((note, index) => {
                    return (
                        <Note key={index}
                              noteIndex={index}
                              note={note}
                              noteId={note.id}
                              notes={notes}
                              setNotes={setNotes}
                              serverText={note.text}
                              connection={connection}
                        />
                    )
                }
            )
            }
        </div>
    )
}


export default App
